import React, {useEffect, useState} from 'react'
import '../CircleBtns_txt/CircleBtnstxt.css'

const CircleTxtBtns = ({title, time, addTime}) => {
        // const {darkMode} = useContext(PaymentContext)
        const darkMode = 1800
        const [darkModeStyle, setDarkModeStyle] = useState({
            buttonStyles: 'global_circle_btns',
            title: 'title'
        })
        // instiate hours for all day calc
        const dateTime = new Date()
        const hour = dateTime.getHours()

    useEffect(()=>{
        if(darkMode >= 1800 || darkMode <= 600){
            setDarkModeStyle({
                buttonStyles: 'global_circle_btns_dark', 
                title: 'title_dark'
            })
        }
        if(darkMode < 1800 || darkMode > 600){
            setDarkModeStyle({
                buttonStyles: 'global_circle_btns', 
                title: 'title'
            })
        }
        // Case all day? set Conditions 18 refs 6pm & 6 ref 6am
        if(time == 0){
            switch(hour){ 
                case (hour < 18 && hour > 6?hour:''): 
                const calc_day_time = 18 - hour
                setTimer({...timer, setTime: parseFloat(`0.0${calc_day_time}`) })
                break;
                case (hour > 18 && hour < 24?hour: ''): 
                const calc_late_night = (24 - hour) + 6
                setTimer({...timer, setTime: parseFloat(`0.0${calc_late_night}`) })
                break;
                case (hour < 6?hour: ''): 
                const calc_early_morning = 6 - hour
                setTimer({...timer, setTime: parseFloat(`0.0${calc_early_morning}`) })
                break;
            }
        } 
    }, [])

    const [timer, setTimer] = useState({
        title: title,
        setTime: parseFloat(time)
    })









    const incrementTime = () =>{

    console.log(timer.setTime)
     addTime(timer.setTime)
    }
    return (
        <div className={`${darkModeStyle.buttonStyles} content-align`} onClick={incrementTime}>
            <span className={darkModeStyle.title}>{timer.title}</span>
        </div>
    )
}

export default CircleTxtBtns;
