import React, { useState } from 'react'
import { CardNumberElement, CardCvcElement, CardExpiryElement, useElements, useStripe } from '@stripe/react-stripe-js'
import Lock from '../Assets/lock.png'
import CreditCardIcon from '../Assets/ccIcon.svg'
import axios from 'axios'
import '../Pages/pages.css'


const StripePayment = () => {
    // using state to keep track of stripe payment, show errors, and manange user interface
    const [success, setSuccess] = useState(false)
    const [error, setError] = useState(null)
    const [processing, setProcessing] = useState('')
    const [disabled, setDisabled] = useState(true)
    const [clientSecret, setClientSecrete] = useState('')
    const [card_info, setCard_info] = useState({
        number: 0,
        expiry_: 0,
        cvc_: 0,
        zip_: 1090,
        set: false
    })

    const stripe = useStripe()
    const element = useElements()

    const handleSubmit = async (e) => {
        e.preventDefault()

        setCard_info({
            number: CardNumberElement,
            expiry_: CardExpiryElement,
            cvc_: CardCvcElement,
            zip_: 1090,
            set: true
        })
        if(card_info.set){
            const { error, paymentMethod } = await stripe.createPaymentMethod({
                type: 'card',
                card: {
                    number: element.getElement(CardNumberElement),

                }
            })
            console.log(paymentMethod.card)
        }



        // if(!error){
        //     try{
        //         const {id} = paymentMethod
        //         const response = await axios.post("http://localhost:4000/payment", {
        //             name: 'Bottle Cap', 
        //             phone: '6470000000', 
        //             licensePlate: 'CLYKF', 
        //             amount: 250, 
        //             id
        //         })
        //         if(response.data.success){
        //             console.log("SuccessFul Payment")
        //             setSuccess(true)
        //         }

        //     }catch(error){
        // console.log('[error'. error)
        //     }
        // }

    } // end of handle submit
    return (
        <>
            <div className='global_container choose_lot'>
                <div>
                    <h1 className='global_content_title slim' style={{ textAlign: 'center', marginTop: '-50%' }}>Enter your card's information below</h1>
                    <form onSubmit={handleSubmit} className='card_'> {/* this should be a form */}
                        {/* Enter your card numner */}
                        <div className='stacked_'>
                            <label id='CCnumberLabel'>Enter Your Card Number</label>
                            <div id='CCnumber'>
                                <CardNumberElement />
                            </div>
                        </div>
                        {/* Expiration CVC & Icon */}
                        <div className='auth_holder'>
                            <div className='stacked_'>
                                <label>Expiration</label>
                                <input id='expiration'></input>
                                <CardExpiryElement />
                            </div>
                            <div className='stacked_'>
                                <label>CVC</label>
                                <CardCvcElement />
                                <input id='cvc'></input>
                            </div>
                            <div>
                                <img className='ccIcon' src={CreditCardIcon} alt='creditCard_logo'></img>
                            </div>
                        </div>
                        <div className='securePaymentHolder'>
                            <div className='securePayment_'>  {/* display flex */}
                                {/* image */}
                                <img className='lock' src={Lock}></img>
                                <p>Secure Payment</p>
                                {/* text info */}
                            </div>
                        </div>

                        {/* Button here */}
                        <div className='ccCardSubmissionHolder'>
                            {stripe ?
                                <input style={{ color: '#FF5759' }} className='submitBtn box-shadow ccCardSubmission' type='submit' value='Pay Now'></input>
                                :
                                ''
                            }

                        </div>


                    </form>
                </div>
            </div>
        </>
    )
}
export default StripePayment;



