import React, { useEffect, useState,useContext } from 'react'
import { PaymentContext } from '../../../Context/PaymentContext'






const PaymentGenerator = (hours, minutes) => {
    const {setBilling, setTime, setNightTime} = useContext(PaymentContext)
    const timeDate = new Date()

    // `${timeDate.getHours()}${timeDate.getMinutes()}`
    // What is the rate per hour? (R$4)
    const night_hourly_rate = 8
    const hourly_rate = 4
    // what is the rate per 15mins (R$1)
    const night_minute_rate = 2
    const minute_rate = 1

    var currentTime = parseInt(`${timeDate.getHours()}${timeDate.getMinutes()}`)
    var setLimit = parseInt(`${hours}${minutes}`)
    var accumalatedTime = currentTime + setLimit
    var minuteCheck = accumalatedTime % 100
    if( minuteCheck > 60){
        var newMinutes = minuteCheck - 60
        var newHours = (accumalatedTime / 100) + 1
        if(newMinutes < 10){
            newMinutes = `0${newMinutes}`
        }

     console.log(newMinutes)
     console.log(newHours)
    }

    // To calculate rate per hour (if hour is > 0 then hour times rate )
    //  To calculate rate per minute (minute / 15 * rate per minute)
    useEffect(()=>{

        // if currentHour  is > 6 then setNightTIme = current_time - 

        setBilling(hours * hourly_rate + (minutes / 15) * minute_rate)
        // Set Selected time here for tracking and to be pushed to local storage by confirm BTN
        setTime(parseInt(`${hours}${minutes}`))
    }, [hours, minutes])

}



export default PaymentGenerator;
