    count = setLocalStorage('count', count -1);
    if(count <= 0){
        clearInterval(counter);
        return;
    }
    setCountDown({
        hours: Math.floor(countDown.mins / 60),
        mins: Math.floor(count / 60),
        secs: count % 60
