import React from 'react'
import Picker from 'react-scrollable-picker'
import { useState } from 'react/cjs/react.development'
import ScrollableDatePicker from '../Components/ScrollableDatePicker/ScrollableDatePicker'

/**
* @author
* @function LongTimeSelection
**/

const LongTimeSelection = (props) => {
var mins = []
var hrs = []
var month_days = []
const date = new Date()
const get_days_of_month = (month, year) => { 
    return new Date(year, month, 0).getDate()
}
const days_in_current_month = get_days_of_month(date.getMonth(), date.getFullYear())
const options = {month: 'long'}
const current_month = new Intl.DateTimeFormat('en-US', options).format()

for(var i = 0; i < 60; i++){ 
    mins.push({value: i, label: i})            
}
for(var i = 0; i < 12; i++){ 
    hrs.push({value: i, label: i})            
}
for(var i = date.getDate(); i < days_in_current_month; i++){
month_days.push({value:`${current_month} ${i}`, label: `${current_month} ${i}`})
}



  return(
 <>
<ScrollableDatePicker hours={hrs} minutes={mins}/>
 </>
   )

 }

 export default LongTimeSelection;



