import React, { Component } from 'react'




class Time extends Component {
    constructor(props) {
        super(props)
        this.meridian_units = ['am', 'pm']
        this.__init_date__ = new Date()
        this.current_hour = this.__init_date__.getHours()
        this.current_mins = this.__init_date__.getMinutes()
        this.meridian_units_switch = this.current_hour > 12? true: false
        this.output_hour = ''
        this.sum_hours = null
        this.days = 0
        this.output_meridian = ''
    }

    set_expTime_to_secs = () =>{ 
        var output = ((this.sum_hours * 3600) + (this.current_mins * 60))
        return output
    }

    start_time = () =>{ 
        return `${this.current_hour}:${this.current_mins}${this.output_meridian}`
    }

    genrate_expired_time = (scrolled_hours) => {
        this.sum_hours = this.current_hour + scrolled_hours
        var set_hour_limitors = [12, 24, 36, 48]
        this.output_hour = this.sum_hours


        // mins check
        if (this.current_mins == 0) {
            this.current_mins = '00'
        }

        for (var i = 0; i < set_hour_limitors.length; i++) {
            if (this.sum_hours >=set_hour_limitors[i]) {
                var iterate_set_hour_limitors = this.sum_hours - set_hour_limitors[i]
                this.output_hour = iterate_set_hour_limitors == 0?12:iterate_set_hour_limitors
            }
            if(this.sum_hours >= set_hour_limitors[i]){ 
                this.meridian_units_switch = !this.meridian_units_switch // this switches onload
            }
            else{
                this.meridian_units_switch = this.meridian_units_switch
            }
        }
        this.set_expTime_to_secs()
        console.log(this.meridian_units_switch)
        this.output_meridian = this._switch_meridian_unit(this.meridian_units_switch)
        return `${this.output_hour}:${this.current_mins < 10 ? `0${this.current_mins}` : this.current_mins}${this.output_meridian}`
    }



    _switch_meridian_unit = (meridian_ref) => {
        console.log(this.meridian_units_switch)
        if(meridian_ref){
            return this.meridian_units[1]
        }
        else if(!meridian_ref){ 
            return this.meridian_units[0]
        }
    }


    button_max_calculation = () => { 
        if(this.output_meridian == this.meridian_units[0]){ 

        }
        if(this.output_meridian == this.meridian_units[1]){ 
            console.log(24 - (this.output_hour + 12) + 6)
        }

    }
    // Let's assume 6 is the limit for all time change. 
    // So 6am & 6pm determines if it's night time rate or day time rate. 
    // We need to do the following.

    // First we check to see Which time of the day we are in. This provides our limit Eg. 1800
    // As per lot. We get the hourly cap base on price. Which means if the max rate is $5 and per hour is $1
    // Then anything greater than 5 and lesser than 1800 will be considered Max Rate 
    // Else anything lesser than 5 hours will be calculated at daily rate. 
    // if calculated at daily rate surpasses the time cap then it's shift to nightly rate 
    // 


}








