import { Component } from 'react'
import PaymentGenerator from '../Financials/Calculator/PaymentGenerator'
import Tick from './../../Assets/tick.mp3'
import {Howl, Howler} from 'howler'

// const pGenerator = new PaymentGenerator()

class TimerLogic extends Component {
    constructor(props){
        super(props)
        this.state = {
            hours: 0,
            stagedMinutes: 0,
            value: 0.0
        }
        this.sound = new Howl({
            src: [Tick], 
            volume: 0.7
        })


    }



setValue = valFunc =>{
this.value = valFunc
}

stepValue = v =>{ // v stands for value
    if(v > 1){
        return 1
    }else{
        return Math.round(v * 100) / 100 // denotes the amount of steps to take. 5 is assigned to 5 steps given a value of 0.2
    }
}


scrollTime = stepVal => {
    var givenValue = Math.round(stepVal*100)
    var minutes =  0
    console.log(`Given Value: ${givenValue}`)
    this.hours = givenValue


    PaymentGenerator(this.hours, this.stagedMinutes)
    // Sound generator
    // this.sound.play()

    return(

    parseInt(`${this.hours}${minutes}`)
    // `0${this.hours}h : ${this.stagedMinutes}m`
    )


}










} // End of Class

export default TimerLogic


