import React, { useEffect, useState } from 'react'
import '../CircleBtns_txt/CircleBtnstxt.css'

const CircleTxtBtns = ({ title, time, addTime, commit }) => {
    // const {darkMode} = useContext(PaymentContext)
    const darkMode = 1800
    const [darkModeStyle, setDarkModeStyle] = useState({
        buttonStyles: 'global_circle_btns',
        title: 'title'
    })

    const [timer, setTimer] = useState({
        title: title,
        setTime: parseFloat(time)
    })

    const incrementTime = () => {
        addTime(timer.setTime, commit)
    }

    const date = new Date()
    const hour = date.getHours()
    const limit = 18

    useEffect(() => {
        if (darkMode >= 1800 || darkMode <= 600) {
            setDarkModeStyle({
                buttonStyles: 'global_circle_btns_dark',
                title: 'title_dark'
            })
        }
        if (darkMode < 1800 || darkMode > 600) {
            setDarkModeStyle({
                buttonStyles: 'global_circle_btns',
                title: 'title'
            })
        }


        if (time == 0) {
            switch (hour) {
                case (hour < limit && hour > 6 ? hour : ''):
                    const remaining_time = limit - hour
                    setTimer({...timer, setTime: `0.0${remaining_time}`})
                    break;
                case (hour > limit && hour < 24?hour:''): 
                    const rem_time =(24 - hour) + 6
                    setTimer({...timer, setTime: `0.0${rem_time}`})
                break;
            }
        }


    }, [])


    return (
        <div className={`${darkModeStyle.buttonStyles} content-align`} onClick={incrementTime}>
            <span className={darkModeStyle.title}>{timer.title}</span>
        </div>
    )
}

export default CircleTxtBtns;
