    const {setBilling, setTime, setNightTime} = useContext(PaymentContext)
    const timeDate = new Date()

    // `${timeDate.getHours()}${timeDate.getMinutes()}`
    // What is the rate per hour? (R$4)
    const night_hourly_rate = 8
    const hourly_rate = 4
    // what is the rate per 15mins (R$1)
    const night_minute_rate = 2
    const minute_rate = 1
    // if(minutes === '00'){ 
    //     console.log('true'
    //     )}
    var currentTime = parseInt(`${timeDate.getHours()}${timeDate.getMinutes()}`)
    var setLimit = parseInt(`${hours}${minutes}`)
    var accumalatedTime = 1700 + setLimit
    var minuteCheck = accumalatedTime % 100
    var hourCheck =  Math.floor(accumalatedTime / 100)
    var newMinutes
    var newHours
    var daytime_billing
    var overlap_billing
    var night_time



    if(hourCheck > 23){
        hourCheck = hourCheck - 24
    }
    if( minuteCheck >= 60){
        newMinutes = minuteCheck - 60
        newHours = Math.floor((hourCheck) + 1)
        accumalatedTime = parseInt(`${newHours}${newMinutes}`)
    if(newMinutes < 10){
            accumalatedTime = parseInt(`${newHours}0${newMinutes}`)
    }
    if(newMinutes === 0){
        accumalatedTime = parseInt(`${newHours}00`)
    }
}else {
        accumalatedTime = 1700 + setLimit //Current time + Set Limit time

    }




    useEffect(()=>{
        // Check to see if the current time is lesser than 6 if so and with added time then do the following. 
        daytime_billing = hours * hourly_rate + (minutes / 15) * minute_rate

        if(accumalatedTime <= 1800){ // current_time < 
            console.log(accumalatedTime) // This would be the regular day time.
            setBilling(daytime_billing)
        }        
        else if(1700 <= 1800 && accumalatedTime > 1800){ //current_time >
            const night_overlap_time = accumalatedTime - 1800
            const night_overlap_cost = (accumalatedTime - 1800) * .1 //.1 is dollar value of 6 dollars per hour
           console.log(night_overlap_time)

            if(night_overlap_time > 99){
            const newNightTIme_hour = Math.floor(night_overlap_time / 100)
            const newNightTIme_min = Math.floor(night_overlap_time % 100)
            //   night_overlap_time / 60
            console.log(`/60: ${newNightTIme_hour}`)
            console.log(`/min: ${newNightTIme_min}`)
            // newNightTime_min < 10 then add the extra 0 
            }
            daytime_billing = hours * hourly_rate + (minutes / 15) * minute_rate
            overlap_billing = Math.round((night_overlap_cost + Number.EPSILON) * 100) / 100

            setBilling(daytime_billing) //daytime billing
                                        // overlap_night_time_billing
            console.log(`daytime billing: ${daytime_billing}`)
            console.log(`Overlap time: ${night_overlap_time}`)
            console.log(`night Cost: $${overlap_billing}`)
        }
        // This makes sure that it the time is over 6pm and just bill for night time rates only
        //if(currentTime > 1800 && accumalatedTime > 1800)
        else if(1700 > 1800 && accumalatedTime > 1800){
            setBilling(daytime_billing) //daytime billing
            console.log('working')
        // 
        }

        // console.log(`limit at: ${setLimit}`)


        // if currentHour  is > 6 then setNightTIme = current_time - 

        // setBilling()
        // Set Selected time here for tracking and to be pushed to local storage by confirm BTN
        setTime(parseInt(`${hours}${minutes}`))
    }, [hours, minutes])

}



export default PaymentGenerator;
