import React, { Component } from 'react'




class Convert_to_meridian extends Component {
    constructor(props) {
        super(props)
        this.meridian_units = ['am', 'pm']
        this.__init_date__ = new Date()
        this.current_hour = this.__init_date__.getHours()
        this.current_mins = this.__init_date__.getMinutes()
        this.meridian_units_switch = this.current_hour > 12? false: true
        this.output_hour = ''
    }



    genrate_expired_time = (scrolled_hours) => {
        var sum_hours = this.current_hour + scrolled_hours
        var set_hour_limitors = [12, 24, 36, 48]
        var output_meridian = ''
        this.output_hour = sum_hours


        // mins check
        if (this.current_mins == 0) {
            this.current_mins = '00'
        }

        for (var i = 0; i < set_hour_limitors.length; i++) {
            if (sum_hours > set_hour_limitors[i]) {
                this.output_hour = sum_hours - set_hour_limitors[i]
                this.meridian_units_switch = !this.meridian_units_switch

            }
        }

        output_meridian = this._switch_meridian_unit(this.meridian_units_switch)
        return `${this.output_hour}:${this.current_mins < 10 ? `0${this.current_mins}` : this.current_mins}${output_meridian}`
    }



    _switch_meridian_unit = (meridian_ref) => {
        console.log(!meridian_ref)
        if(this.meridian_units_switch){
            return this.meridian_units[0]
        }
        else if(meridian_ref){ 
            return this.meridian_units[1]
        }
    }


}








