import { useStripe } from '@stripe/react-stripe-js'
import React, { useState, useContext, useEffect, useRef } from 'react'
import {PaymentContext} from '../Context/PaymentContext'



export const CountDown = () => {
    const { expiredTime } = useContext(PaymentContext)
    const getSetTime = localStorage.getItem('timeStore')
    const [containerStyling, setContainerStyling] = useState({
        divStyle:{
            display:'flex', 
            flexDirection: 'column', 
            alignItems: 'center', 
            justifyContent: 'center'
        },
        buttonStyle:{
            marginTop: '30px',
            border: '1px solid #848484',
            fontSize: '13px',
            // border: '1px solid #FF4F4F',
            padding: '10px',
            borderRadius: '10px'
        },
        buttonDiv: {
            width: '100%',
            display: 'flex',
            justifyContent: 'space-around'
        }
    })
    const [countDown, setCountDown] = useState({
        hours: Math.round(getSetTime / 100),
        mins: getSetTime % 100,
        secs: 60,
        ms: 1000
    })


    // Set mutable variables for updates
    var updated_hours = countDown.hours, updated_mins = countDown.mins,  updated_secs = countDown.secs, updated_ms = countDown.ms

    useEffect(()=>{
        setInterval(()=>{
            setCountDown({
                hours: updated_hours,
                mins: updated_mins, 
                secs: updated_secs,
                ms: updated_ms
            })      
        }, 1000)


    },[])



    const run = () => { 
        updated_secs--
        if(updated_ms === 0){
            updated_secs--
            updated_ms = 100
        }
        if(updated_secs === 0){
            updated_mins--
            updated_secs = 60
        }
        if(updated_mins === 0){
            updated_hours--
            updated_mins = 60
        }

        if(updated_hours === 0){
            updated_hours = '00'
        }
        // console.log(updated_secs)
        setTimeout(run, 1000)
    }
run()
    // setInterval(run, 1000)


    return (
        <div className='global_container choose_lot'>
<div style={containerStyling.divStyle}>
<div className='circleAnimation'></div>
            <div className='countdown_digits_holder'>
                <div className='countdown_adjustment'>
                    <h3 style={{marginBottom: '0px'}}>Active Session</h3>
                <div className='countdown_digits'>
                    <span className='digits'>{`${countDown.hours}h`}</span>
                    <span className='digits'>{`${countDown.mins}m`}</span>
                    <span className='digits' >{`${countDown.secs}`}s</span>
                </div>
                </div>

        </div>
        <h4>Your Time Expires at: 13:41pm</h4>
        <div style={containerStyling.buttonDiv}>
        <span style={containerStyling.buttonStyle}>Extend My Time</span>
        <span style={containerStyling.buttonStyle}>View My Receipt</span>
        </div>

</div>

            {/* Count down time */}
            {/* Buttone to Extend time here */}
        </div>
    )
}
