import React from 'react'
import CreditCardIcon from '../Assets/ccIcon.svg'
import Lock from '../Assets/lock.png'
import {useHistory} from 'react-router-dom'
import {CardElement, useStripe, useElements} from '@stripe/react-stripe-js'; 


const CardPayment = () => {
const history = useHistory()
const stripe = useStripe()
const elements = useElements()
const pay_now = () =>{
    history.push('/remaining-time/')
}


const handleSubmit = async (e) =>{
    e.preventDefault();


    const billingDetails = {
        name: e.target.name.value, 
        email: e.target.email.value, 
        address: {
            city: e.target.city.value, 
        }
    }   

    //CREATE PAYMENT INTENT ON THE SERVER
    const {error: backendError, clientSecret} = await fetch('/create-payment-intent', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            paymentMethod: 'card',
            currency: 'CAD'
        }),
    }).then(r => r.json())

    if(backendError){
        console.log(backendError.message)
        return;
    }

    // CONFIRMTHE PAYMENT ON THE CLIENT
    const {error: stripeError, paymentIntent} = await stripe.confirmCardPayment(
        clientSecret, {
            payment_method: {
                card: elements.getElement(CardElement),
            }
        }
    )

    if(stripeError){
        console.log(stripeError.message)
        return;
    }

    console.log(`PaymentIntent ${paymentIntent.id}: ${paymentIntent.status}`)



    // if stripe and elements not loaded return null
    if(!stripe || !elements){
        return;
    }
       const cardElement = elements.getElement(CardElement)

       const {error, paymentMethod} = await stripe.createPaymentMethod({
           type: 'card',
           card: cardElement
       })
       if(error){
           console.log('[error', error);
       }else{
           console.log('[PaymentMethod]', paymentMethod);
       }
}

const cardElementOptions = {
    style: {
        base:{
            fontSize: '16px',

        },
        invalid:{

        },
        complete: {

        }
    }
}

