import React, { useContext, useEffect, useState } from 'react'
import { PaymentContext } from '../Context/PaymentContext'
import "../Pages/pages.css";
import CircleTxtBtns from "../Components/Buttons/CircleBtns_txt/CircleTxtBtns";
import CircleBtnsImg from "../Components/Buttons/CircleBtn_img/CircleBtnsImg";
import Timer from "../Components/Timer/Timer";


const SetTime = (props) => {
	const { darkMode } = useContext(PaymentContext)
	const [darkModeStyle, setDarkModeStyle] = useState({ globalContainer: 'global_container' })
	const [lot, setLot] = useState(0)
	const [btnClickUpdate, setBtnClickUpdate] = useState(0.0)
	const [val, setVal] = useState(0.0)
	const [check, setCheck] = useState('false')
	const [max, setMax] = useState('0.00')
	var newCheck = false

	// Time update nested func for button onClick()
	const timeUpdate = (val, checked) => {
		var nValue = parseFloat(val)
		setVal(nValue)
		setCheck(checked)

		// check for match
		if (val == btnClickUpdate) {
			reset()
		}
		else (

			setBtnClickUpdate(nValue)
		)
	}
	var localStorage_ = localStorage.getItem('expTime').split(':')
	const ls_Int = parseInt(localStorage_[0]) // We're getting the hour of expTime 
	var max_num = 0.00
	// if (ls_Int == 6) {
	// 	max_num = 12
	// }

	const date = new Date()
	const hour = date.getHours()
	const max_conditions = () => {

		// get the localstorage exp time and match it with 6
		// if match, we're going to assign the max_num variable to 12
		// if it is not 6, then we're going to switch case and return the result to max_num
		if (newCheck == true) {
			max_num = '0.12'
		} else if(newCheck == false) {
			switch (hour) {
				case (hour < 18 && hour > 6 ? hour : ''):
					const remaining_time = 18 - hour
					setMax(parseFloat(`0.0${remaining_time}`))
					newCheck = true
					// console.log(`rem_hr ${remaining_time}`)
					break;
				case (hour >= 18 && hour < 24 ? hour : ''):
					const rem_time = (24 - hour) + 6
					setMax((rem_time > 9 ? max_num = parseFloat(`0.${rem_time}`) : parseFloat(`0.0${rem_time}`)))
					newCheck = true
					break;
				case (hour < 6 ? hour : ''):
					const rem_hr = 6 - hour
					setMax(`0.0${rem_hr}`)
					newCheck = true
					break;
				default: 
					max_num = '0.12'
					break;
			}
		}
	}
	max_conditions()
	console.log(max_num)



	useEffect(() => {

		if (localStorage.getItem('lot') === null) {
			// localStorage.setItem('lot', 4001)
			localStorage.setItem('lot', props.match.params.lot)
		}
		else if (localStorage.getItem('lot') != null) {
			localStorage.removeItem('lot')
			setTimeout(() => {
				localStorage.setItem('lot', props.match.params.lot)
			}, 10)
		}
		setLot(props.match.params.lot)
		if (darkMode >= 1800 || darkMode <= 600) {
			setDarkModeStyle({
				globalContainer: 'global_container_dark',
			})
		}

		if (btnClickUpdate == 0) {
			setBtnClickUpdate(val)
		}
	}, [btnClickUpdate])


	const reset = () => {
		setBtnClickUpdate(0.0)
	}




	// console.log(`let's see: ${max_num}`)

	// console.log(btnClickUpdate)
	return (
		<div className={`${darkModeStyle.globalContainer} choose_lot`}>

			<div id='content_holder'>
				<div className='timer_holder'>
					<div className='lot-info'>
						<h5><strong>LOT NUMBER:</strong> {lot}</h5>
					</div>
					<Timer addTime={btnClickUpdate} checked={check} />
				</div>
				<div className='align_timer_btns'>
					<CircleTxtBtns addTime={timeUpdate} title='-1 Hr' time='-0.01' />
					<CircleTxtBtns addTime={timeUpdate} title='Max' checked={'true'} time={max_num} />
					<CircleTxtBtns addTime={timeUpdate} title='+1 Hr' time='0.01' />
				</div>

				<div className='align_timer_btns btns_img_container'>
					{/* <CircleBtnsImg title='' img='' /> */}
					<CircleBtnsImg title='Confirm' img='' class='plateCapture' />
					{/* <CircleBtnsImg title='' img='' /> */}
				</div>
			</div>
		</div>
	);
};

export default SetTime;
