import React, { useEffect, useState } from 'react'
import CreditCardIcon from '../Assets/ccIcon.svg'
import Lock from '../Assets/lock.png'
import {useHistory} from 'react-router-dom'
import {CardElement, useStripe, useElements} from '@stripe/react-stripe-js'; 


// const axios = require('axios').default;
const CardPayment = () => {


// using state to keep track of stripe payment, show errors, and manange user interface
const [succeeded, setSucceeded] = useState(false)
const [error, setError] = useState(null)
const [processing, setProcessing] = useState('')
const [disabled, setDisabled] = useState(true)
const [clientSecret, setClientSecrete] = useState('')


// extract stripe and element 
const stripe = useStripe()
const elements = useElements()

useEffect(()=>{
    window.fetch('/create-payment-intent', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json', 
            'Accept': 'application/json'
        },
        body: JSON.stringify({items: [{parking_id: 'xl-lot23w'}]})
    }).then(res => {
        return res.json();
    }).then(data => {
        setClientSecrete(data.clientSecret)

    })
}, [])

const handleChange = async (event) =>{
    event.preventDefault()

    //Listen for changes in the CardElement
    // and display any errors as the customer types their card details
    setDisabled(event.empty);
    setError(event.error ? event.error.message : '')

}

const handleSubmimt = async (ev) => {
    ev.preventDefault()
    setProcessing(true)

    if(!stripe || !elements){
        return;
    } 

    const payload = await stripe.confirmCardPayment(clientSecret, {
        payment_method: { 
            card: elements.getElement(CardElement)
        }
    })



}


const cardElementOptions = {
    style: {
        base:{
            fontSize: '16px',

        },
        invalid:{

        },
        complete: {

        }
    }
}



return(
    <div className=''>
    <form>
    <CardElement id='card-element' options={cardElementOptions}/>
    <button type='submit' disabled={!stripe}>
        Pay Now
    </button>
    <div className='card-error' role='alert'> 
    {error}
    </div>
</form>
    </div>
)
}
