import React, { Component } from 'react'



class PaymentGenerator extends Component {
constructor(props){
    super(props)
this.state  = {
    test: 0
}
}


selected_hour_rate = 0
selected_minute_rate = 0
componentDidMount(cost_generator){
    this.cost_generator()
    }

cost_generator = (hours, minutes) =>{

    // What is the rate per hour? (R$4)
    const hourly_rate = 4
    // what is the rate per 15mins (R$1)
    const minute_rate = 1
    // To calculate rate per hour (if hour is > 0 then hour times rate )
    this.selected_hour_rate = hours * hourly_rate
    //  To calculate rate per minute (minute / 15 * rate per minute)
    this.selected_minute_rate = (minutes / 15) * minute_rate
    this.setState({test: 1})
}



rPayment = () =>{
console.log(this.selected_hour_rate)
}


}

export default PaymentGenerator;
