import React, { useState, useContext } from 'react'
import {PaymentContext} from '../Context/PaymentContext'
import { CardNumberElement, CardCvcElement, CardExpiryElement, useElements, useStripe } from '@stripe/react-stripe-js'
import Lock from '../Assets/lock.png'
import CreditCardIcon from '../Assets/ccIcon.svg'
import axios from 'axios'
import '../Pages/pages.css'
import Loading from '../Components/Loading/Loading'
import { withRouter, useHistory } from 'react-router-dom'


const StripePayment = () => {
    // using state to keep track of stripe payment, show errors, and manange user interface
    const [success, setSuccess] = useState(false)
    const [status, setStatus] = useState({
        status: "Enter your card's information below"
    })
    const [processing, setProcessing] = useState('')
    const [disabled, setDisabled] = useState(true)
    const [clientSecret, setClientSecrete] = useState('')
    const {userInfo, totalbilling} = useContext(PaymentContext)


    const stripe = useStripe()
    const element = useElements()
    const history = useHistory()

    const handleSubmit = async (e) => {
        e.preventDefault()

        const { error, paymentMethod } = await stripe.createPaymentMethod({
            type: 'card',
            card: element.getElement(CardNumberElement, CardExpiryElement, CardCvcElement),
        })

        if (!error) {
            try {
                const { id } = paymentMethod
                const response = await axios.post("http://localhost:4000/payment", {
                    name: userInfo.name, 
                    phone: userInfo.phone, 
                    license: userInfo.license,
                    amount: totalbilling * 100,
                    id
                })
                if (response.data.success) {
                    // setTimeOut to Change to next page
                    setTimeout(()=>{
                        history.push('/remaining-time/')
                    }, 2000)
                        console.log("SuccessFul Payment")
                        setSuccess(true)


                }

            } catch (error) {
                // Change the title information
                setStatus({
                    status: error
                })
            }
        }

    } // end of handle submit
    return (
        <>
            <div className='global_container choose_lot'>
                {stripe? 

                <div>
                    <h1 className='global_content_title slim' style={{ textAlign: 'center', marginTop: '-50%' }}>{status.status}</h1>
                    <form onSubmit={handleSubmit} className='card_'> {/* this should be a form */}
                        {/* Enter your card numner */}
                        <div className='stacked_'>
                            <label id='CCnumberLabel'>Enter Your Card Number</label>
                            <div id='CCnumber'>
                                <CardNumberElement />
                            </div>
                        </div>
                        {/* Expiration CVC & Icon */}
                        <div className='auth_holder'>
                            <div className='stacked_'>
                                <label>Expiration</label>
                                <div id='expiration'>
                                    <CardExpiryElement />
                                </div>

                            </div>
                            <div className='stacked_'>
                                <label>CVC</label>
                                <div id='cvc'>
                                    <CardCvcElement />
                                </div>
                            </div>
                            <div>
                                <img className='ccIcon' src={CreditCardIcon} alt='creditCard_logo'></img>
                            </div>
                        </div>
                        <div className='securePaymentHolder'>
                            <div className='securePayment_'>  {/* display flex */}
                                {/* image */}
                                <img className='lock' src={Lock}></img>
                                <p>Secure Payment</p>
                                {/* text info */}
                            </div>
                        </div>

                        {/* Button here */}
                        <div className='ccCardSubmissionHolder'>
                            <input style={success? { color: 'green', border: '1px solid green !important' }:{ color: '#FF5759',  border: '1px solid #FF5759 !important' }} className='submitBtn box-shadow ccCardSubmission' type='submit' value={success? 'Approved!': 'Pay Now'}></input>
                        </div>


                    </form>
                </div>
                :
                <Loading />
}
            </div>
        </>
    )
}
export default StripePayment;
