        var newMinutes = minuteCheck - 60
        var newHours = Math.floor((hourCheck) + 1)
        if(newMinutes < 10){
            newMinutes = `0${newMinutes}`
        }
        accumalatedTime = parseInt(`${newHours}${newMinutes}`)
    } else {
        accumalatedTime = currentTime + setLimit
    }



    // To calculate rate per hour (if hour is > 0 then hour times rate )
    //  To calculate rate per minute (minute / 15 * rate per minute)
    useEffect(()=>{

        // if(accumalatedTime > 1800){
        //     setNightTime()
        // }
