import React from 'react'
import {CardElement, useStripe, useElements} from '@stripe/react-stripe-js'; 
// import PaymentButtons from '../Components/Buttons/PayOptions/PaymentButtons'
// import Google from '../Assets/Google.svg'
// import CreditCardIcon from '../Assets/ccIcon.svg'
// import Apple from '../Assets/Apple.png'



const  Selectmethod = () =>{
const stripe = useStripe()
const elements = useElements()

const handleSubmit = async (e) =>{
    e.preventDefault();

    // if stripe and elements not loaded return null
    if(!stripe || !elements){
        return;
    }
       const cardElement = elements.getElement(CardElement)

       const {error, paymentMethod} = await stripe.createPaymentMethod({
           type: 'card',
           card: cardElement
       })
       if(error){
           console.log('[error', error);
       }else{
           console.log('[PaymentMethod]', paymentMethod);
       }
}



const cardElementOptions = {
    style: {
        base:{
            fontSize: '16px',

        },
        invalid:{

        },
        complete: {

        }
    }
}






return(
<div className='global_container choose_lot'>
<div className='stacked_container'>
<h1 className='global_content_title slim'>Choose a payment method</h1>

<div className='position-center'>
<div className='stacked'>
<form onSubmit={handleSubmit}>
    <input type='text' name='name' placeholder='Jane Doe' required></input>
    <input type='email' name='email' placeholder='JaneDoe@example.com' required></input>
    <input type='address' name='address' placeholder='1 place rd' required></input>
    <input type='text' name='city' placeholder='Toronto' required></input>


    <CardElement options={cardElementOptions}/>
    <button type='submit' disabled={!stripe}>
        Pay Now
    </button>
</form>
{/* <PaymentButtons title='Google' image={Google}/>
<PaymentButtons title='Apple' image={Apple}/>
<PaymentButtons title='Pay by Card' image={CreditCardIcon}/> */}

</div>
</div>

</div>

</div>
)

}

export default  Selectmethod;
